}

.drop-zone {
  border: 2px dashed hsl(var(--muted-foreground) / 0.3);
  transition: all 0.2s ease;
}

.drop-zone.active,
.drop-zone:hover {
  border-color: hsl(var(--primary));
  background-color: hsl(var(--primary) / 0.05);
}

.module-card {
