import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useEditorStore } from "@/store/editor";
import { useToast } from "@/hooks/use-toast";
import { Search, X } from "lucide-react";
import type { Template } from "@shared/schema";

export default function TemplateLibrary() {
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("all");
  const { 
    isTemplateLibraryOpen, 
    setTemplateLibraryOpen, 
    setElements, 
    setGlobalStyles,
    currentProject
  } = useEditorStore();
  const { toast } = useToast();

  const { data: templates, isLoading } = useQuery<Template[]>({
    queryKey: ["/api/templates"],
    enabled: isTemplateLibraryOpen,
  });

  const useTemplateMutation = useMutation({
    mutationFn: async (template: Template) => {
      // Apply template to current project
      setElements(Array.isArray(template.elements) ? template.elements : []);
      setGlobalStyles(template.globalStyles && typeof template.globalStyles === 'object' ? template.globalStyles as any : {
        colors: {
          primary: "#2563eb",
          secondary: "#64748b",
          accent: "#7c3aed", 
          background: "#ffffff",
          foreground: "#0f172a"
        },
        fonts: {
          primary: "Inter",
          secondary: "Georgia",
          mono: "Menlo"
        },
        spacing: {
          scale: 1,
          unit: "rem"
        }
      });
    },
    onSuccess: () => {
      setTemplateLibraryOpen(false);
      toast({
        title: "Template applied",
        description: "The template has been successfully applied to your project.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to apply template. Please try again.",
        variant: "destructive",
      });
    },
  });

  const categories = [
    { id: "all", label: "All Templates" },
    { id: "Landing Page", label: "Landing Page" },
    { id: "Business", label: "Business" },
    { id: "Portfolio", label: "Portfolio" },
    { id: "E-commerce", label: "E-commerce" },
    { id: "Blog", label: "Blog" },
  ];

  const filteredTemplates = templates?.filter(template => {
    const matchesSearch = template.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         template.description?.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesCategory = selectedCategory === "all" || template.category === selectedCategory;
    return matchesSearch && matchesCategory;
  }) || [];

  if (!isTemplateLibraryOpen) return null;

  return (
    <Dialog open={isTemplateLibraryOpen} onOpenChange={setTemplateLibraryOpen}>
      <DialogContent className="max-w-6xl w-full max-h-[90vh] overflow-hidden p-0">
        {/* Modal Header */}
        <DialogHeader className="p-6 border-b border-border">
          <div className="flex items-center justify-between">
            <div>
              <DialogTitle className="text-2xl font-semibold">Template Library</DialogTitle>
              <DialogDescription className="mt-1">
                Choose from professionally designed templates to get started quickly
              </DialogDescription>
            </div>
            <Button 
              variant="ghost" 
              size="sm"
              onClick={() => setTemplateLibraryOpen(false)}
              data-testid="button-close-template-library"
            >
              <X className="w-5 h-5" />
            </Button>
          </div>

          {/* Search */}
          <div className="relative mt-4">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground w-4 h-4" />
            <Input
              placeholder="Search templates..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="pl-9"
              data-testid="input-search-templates"
            />
          </div>
        </DialogHeader>

        {/* Template Categories */}
        <Tabs value={selectedCategory} onValueChange={setSelectedCategory} className="flex-1 flex flex-col">
          <TabsList className="grid grid-cols-6 w-full border-b border-border rounded-none h-auto">
            {categories.map((category) => (
              <TabsTrigger 
                key={category.id} 
                value={category.id}
                className="py-3 px-6 data-[state=active]:border-b-2 data-[state=active]:border-primary"
                data-testid={`tab-category-${category.id}`}
              >
                {category.label}
              </TabsTrigger>
            ))}
          </TabsList>

          {categories.map((category) => (
            <TabsContent 
              key={category.id} 
              value={category.id} 
              className="flex-1 p-6 overflow-y-auto"
              style={{ maxHeight: "60vh" }}
            >
              {isLoading ? (
                <div className="grid grid-cols-3 gap-6">
                  {[...Array(6)].map((_, i) => (
                    <div key={i} className="animate-pulse">
                      <div className="aspect-[4/3] bg-muted rounded-lg mb-3"></div>
                      <div className="h-4 bg-muted rounded w-3/4 mb-2"></div>
                      <div className="h-3 bg-muted rounded w-1/2"></div>
                    </div>
                  ))}
                </div>
              ) : filteredTemplates.length > 0 ? (
                <div className="grid grid-cols-3 gap-6">
                  {filteredTemplates.map((template) => (
                    <div key={template.id} className="group cursor-pointer">
                      <div className="relative overflow-hidden rounded-lg border border-border bg-muted aspect-[4/3] mb-3">
                        {template.previewImage ? (
                          <img 
                            src={template.previewImage} 
                            alt={template.name}
                            className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-300"
                          />
                        ) : (
                          <div className="w-full h-full flex items-center justify-center text-muted-foreground">
                            No preview available
                          </div>
                        )}
                        <div className="absolute inset-0 bg-gradient-to-t from-black/60 to-transparent opacity-0 group-hover:opacity-100 transition-opacity">
                          <div className="absolute bottom-4 left-4 right-4">
                            <Button
                              className="w-full"
                              onClick={() => useTemplateMutation.mutate(template)}
                              disabled={useTemplateMutation.isPending}
                              data-testid={`button-use-template-${template.id}`}
                            >
                              {useTemplateMutation.isPending ? "Applying..." : "Use Template"}
                            </Button>
                          </div>
                        </div>
                      </div>
                      <div>
                        <h3 className="font-medium text-foreground" data-testid={`text-template-name-${template.id}`}>
                          {template.name}
                        </h3>
                        {template.description && (
                          <p className="text-sm text-muted-foreground" data-testid={`text-template-description-${template.id}`}>
                            {template.description}
                          </p>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="text-center py-12">
                  <p className="text-muted-foreground">
                    {searchQuery 
                      ? `No templates found matching "${searchQuery}"` 
                      : `No templates available in ${category.label}`
                    }
                  </p>
                </div>
              )}
            </TabsContent>
          ))}
        </Tabs>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/editor/toolbar.tsx.content:
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useEditorStore } from "@/store/editor";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { generateHTMLExport } from "@/lib/export";
import { 
  Box, 
  Plus, 
  Save, 
  Download, 
  Rocket, 
  Monitor, 
  Tablet, 
  Smartphone,
  History,
  Undo,
  Redo
} from "lucide-react";
import { Link } from "wouter";

export default function Toolbar() {
  const { 
    currentProject, 
    elements, 
    globalStyles, 
    deviceView, 
    setDeviceView, 
    zoomLevel, 
    setZoomLevel,
    setTemplateLibraryOpen,
    canUndo,
    canRedo,
    undo,
    redo
  } = useEditorStore();

  const { toast } = useToast();

  const saveProjectMutation = useMutation({
    mutationFn: async () => {
      if (!currentProject) throw new Error("No project loaded");

      const response = await apiRequest("PUT", `/api/projects/${currentProject.id}`, {
        elements,
        globalStyles,
        updatedAt: new Date()
      });
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Project saved",
        description: "Your changes have been saved successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Save failed", 
        description: "Failed to save your project. Please try again.",
        variant: "destructive",
      });
    },
  });

  const exportMutation = useMutation({
    mutationFn: async () => {
      if (!currentProject) throw new Error("No project loaded");

      const response = await apiRequest("POST", `/api/projects/${currentProject.id}/export`, {});
      const blob = await response.blob();

      // Download the file
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `${currentProject.name}.html`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      window.URL.revokeObjectURL(url);
    },
    onSuccess: () => {
      toast({
        title: "Export successful",
        description: "Your landing page has been exported as HTML.",
      });
    },
    onError: () => {
      toast({
        title: "Export failed",
        description: "Failed to export your project. Please try again.",
        variant: "destructive",
      });
    },
  });

  return (
    <header className="bg-[hsl(var(--card))] border-b border-[hsl(var(--border))] h-16 flex items-center justify-between px-4 shadow-sm bg-[hsl(var(--primary))/0.02]">
      <div className="flex items-center space-x-4">
        <Link href="/" className="flex items-center space-x-2 hover:opacity-80 transition-all hover:scale-105">
          <div className="w-8 h-8 bg-[hsl(var(--primary))] rounded-lg flex items-center justify-center shadow-md hover:shadow-lg transition-all">
            <Box className="w-4 h-4 text-[hsl(var(--primary-foreground))]" />
          </div>
          <h1 className="text-lg font-semibold text-[hsl(var(--foreground))]">PageBuilder Pro</h1>
        </Link>

        <div className="flex items-center space-x-2 ml-8">
          <Button 
            variant="secondary" 
            size="sm"
            onClick={() => setTemplateLibraryOpen(true)}
            className="ring-1 ring-[hsl(var(--primary))/0.1] hover:ring-2 hover:ring-[hsl(var(--primary))/0.2] hover:bg-[hsl(var(--primary))/0.05]"
            data-testid="button-new-page"
          >
            <Plus className="w-4 h-4 mr-2" />
            New Page
          </Button>
          <Button 
            size="sm" 
            onClick={() => saveProjectMutation.mutate()}
            disabled={saveProjectMutation.isPending}
            className="shadow-sm hover:shadow-md"
            data-testid="button-save"
          >
            <Save className="w-4 h-4 mr-2" />
            {saveProjectMutation.isPending ? "Saving..." : "Save"}
          </Button>
        </div>
      </div>

      {/* Device Preview Controls */}
      <div className="flex items-center space-x-2 bg-[hsl(var(--muted))] rounded-lg p-1 ring-1 ring-[hsl(var(--primary))/0.1]">
        <Button
          variant={deviceView === "desktop" ? "default" : "ghost"}
          size="sm"
          onClick={() => setDeviceView("desktop")}
          className={deviceView === "desktop" ? "shadow-sm" : "hover:bg-[hsl(var(--primary))/0.06]"}
          data-testid="button-desktop-view"
        >
          <Monitor className="w-4 h-4 mr-1" />
          Desktop
        </Button>
        <Button
          variant={deviceView === "tablet" ? "default" : "ghost"}
          size="sm" 
          onClick={() => setDeviceView("tablet")}
          className={deviceView === "tablet" ? "shadow-sm" : "hover:bg-[hsl(var(--primary))/0.06]"}
          data-testid="button-tablet-view"
        >
          <Tablet className="w-4 h-4 mr-1" />
          Tablet
        </Button>
        <Button
          variant={deviceView === "mobile" ? "default" : "ghost"}
          size="sm"
          onClick={() => setDeviceView("mobile")}
          className={deviceView === "mobile" ? "shadow-sm" : "hover:bg-[hsl(var(--primary))/0.06]"}
          data-testid="button-mobile-view"
        >
          <Smartphone className="w-4 h-4 mr-1" />
          Mobile
        </Button>
      </div>

      <div className="flex items-center space-x-4">
        {/* Version Control */}
        <div className="flex items-center space-x-2">
          <span className="text-xs bg-[hsl(var(--cat-content))] text-white px-2 py-1 rounded-full font-medium shadow-sm">
            v1.0
          </span>
          <Button 
            variant="ghost" 
            size="sm"
            disabled={!canUndo}
            onClick={undo}
            className="hover:bg-[hsl(var(--primary))/0.1] disabled:opacity-50"
            data-testid="button-undo"
          >
            <Undo className="w-4 h-4" />
          </Button>
          <Button 
            variant="ghost" 
            size="sm"
            disabled={!canRedo}
            onClick={redo}
            className="hover:bg-[hsl(var(--primary))/0.1] disabled:opacity-50"
            data-testid="button-redo"
          >
            <Redo className="w-4 h-4" />
          </Button>
          <Button 
            variant="ghost" 
            size="sm" 
