                order: 0
              },
              {
                id: "corp-text",
                type: "text",
                content: { text: "We help businesses achieve their goals with innovative solutions and expert guidance." },
                styles: { 
                  fontSize: "1.125rem", 
                  color: "#475569", 
                  textAlign: "center",
                  maxWidth: "42rem",
                  marginLeft: "auto",
                  marginRight: "auto"
                },
                settings: {},
                children: [],
                parentId: "corp-hero",
                order: 1
              }
            ],
            globalStyles: {
              colors: {
                primary: "#0f172a",
                secondary: "#475569",
                accent: "#2563eb",
                background: "#ffffff",
                foreground: "#0f172a"
              },
              fonts: {
                primary: "Inter",
                secondary: "Georgia", 
                mono: "Menlo"
              },
              spacing: {
                scale: 1,
                unit: "rem"
              }
            },
            isPremium: false
          }
        ];

        await db.insert(templates).values(sampleTemplates);
      }
    } catch (error) {
      console.error("Failed to initialize templates:", error);
    }
